import D20.Money
alias D20.Item

defmodule D20 do
  @alignments [
    "Chaotic Evil",
    "Neutral Evil",
    "Lawful Evil",
    "Chaotic Neutral",
    "Neutral",
    "Lawful Neutral",
    "Chaotic Good",
    "Neutral Good",
    "Lawful Good"
  ]
  def alignments, do: @alignments

