    %Item{cost: 0, weight: 0, name: "Censer"},
    %Item{cost: 0, weight: 0, name: "Vestments"},
    %Item{cost: 0, weight: 0, name: "Bag of sand"},
    %Item{cost: 0, weight: 0, name: "Knife (small)"},
    %Item{cost: 2500, weight: 5, name: "Book of lore"}
